customOperator.baseValue:
mFac1:ev(s-iA,simp);
mFac2:ev(s-iB,simp);
mAt:ev(iA*t,simp);
mBt:ev(iB*t,simp);
mQues:ev(expand(mFac1*mFac2),simp); 
mNumA:ev(iAB/(iA*(iA-iB)),simp);
mNumB:ev(iAB/(iB*(iB-iA)),simp);
mVs:ev(iNumA/s+mNumA/(s-iA)+mNumB/(s-iB),simp);
mAns:ev(1+mNumA*e^mAt+mNumB*e^mBt,simp);


nResp:nterms(RESPONSE);
nAns:nterms(mAns);
termsOK:is(is(nResp=nAns)=true);
mAnsDifference:ev(ev(RESPONSE,simp)-ev(mAns,simp),simp);
isEqual:is(is(mAnsDifference=0)=true);
mTransDifference:ev(ev(RESPONSE,simp)-ev(mVs,simp),simp);
isTrans:is(is(mTransDifference=0)=true);

mRcoeff1:ev(coeff(RESPONSE,e^(iA*t)),simp);
mRcoeff2:ev(coeff(RESPONSE,e^(iB*t)),simp);
mRcoeff3:ev(ev(RESPONSE-mRcoeff1*e^(iA*t)-mRcoeff2*e^(iB*t),simp),simp);
t3Number:numberp(mRcoeff3);

t1OK:is(is(ev(mRcoeff1-mNumA,simp)=0)=true);
t2OK:is(is(ev(mRcoeff2-mNumB,simp)=0)=true);
t3OK:is(is(ev(mRcoeff3-iNumA,simp)=0)=true);

t1BadSign:is(is(ev(mRcoeff1+mNumA,simp)=0)=true);
t2BadSign:is(is(ev(mRcoeff2+mNumB,simp)=0)=true);
t3BadSign:is(is(ev(mRcoeff3+iNumA,simp)=0)=true);

t1Zero:is(is(mRcoeff1=0)=true);
t2Zero:is(is(mRcoeff2=0)=true);
t3Zero:is(is(mRcoeff3=0)=true);

oInput:RESPONSE;
