customOperator.baseValue:
iA1: iD;

iA2: ev((-3)*iD*(iA+iB)/2,simp);

iA3: ev(3*iD*iA*iB,simp);

iA4: iC;

iB4: 0;

iB3: 1;

iB2: 2;

iB1: 3;

choose(list) := list[1+random(length(list))];

varNames:[a,b,c,d,h,k,m,n,p,q,r,s,t,u,v,w,x,y,z];

mX:choose(varNames);

varNames: delete(mX,varNames);

mY:choose(varNames);

mQues: ev(iA1*x^iB1+iA2*x^iB2+iA3*x^iB3+iA4*x^iB4,simp);

mDeriv: ev(diff(mQues,x),simp);

mDeriv2: ev(diff(mDeriv,x),simp);

mDerivAm: ev(subst(iA-1/2,x,mDeriv),simp);

mDerivAp: ev(subst(iA+1/2,x,mDeriv),simp);

mDerivBm: ev(subst(iB-1/2,x,mDeriv),simp);

mDerivBp: ev(subst(iB+1/2,x,mDeriv),simp);

iYA: ev(subst(iA,x,mQues),numer,simp);

iD2A: ev(subst(iA,x,mDeriv2),numer,simp);

iYB: ev(subst(iB,x,mQues),numer,simp);

iD2B: ev(subst(iB,x,mDeriv2),numer,simp);

mDerivFactors: ev((x-iA)*(x-iB),simp);
