customOperator.baseValue:
simp:true;

mValX: ev(iA,simp);

iA1: ev(iC,simp);

iA2: ev(-(iA*iC+iD+iB*iC),simp);

iA3: ev(iA*iD+iA*iB*iC+iB*iD,simp);

iA4: ev(-iA*iB*iD,simp);

iB1: ev(iC,simp);

iB2: ev(-(iD+iB*iC),simp);

iB3: ev(iB*iD,simp);

iB4: 0;

iAB1: ev(iA*iB1,simp);

iAB2: ev(iA*iB2,simp);

iAB3: ev(iA*iB3,simp);

iK: ev(iA4,simp);

mX: x;

mY: y;

mQues: ev(iA1*mX^3+iA2*mX^2+iA3*mX+k,simp);

mCubic: ev(iA1*mX^3+iA2*mX^2+iA3*mX+iA4,simp);
mQuad:  ev(iB1*mX^2+iB2*mX+iB3,simp);
mFactor1: ev(mX-iA,simp);
mFactor2: ev(mX-iB,simp);
mFactor3: ev(iC*mX-iD,simp);
mRoot: ev(iD/iC,simp);

iT1: ev(iA1*iA^3,simp);
iT2: ev(iA2*iA^2,simp);
iT3: ev(iA3*iA,simp);
if is(iT2>=0)=true then mSignT2:maOperator("+") else mSignT2:maOperator("-");
iT2:abs(iT2);
if is(iT3>=0)=true then mSignT3:maOperator("+") else mSignT3:maOperator("-");
iT3:abs(iT3);

iAbsA2:abs(iA2);
iAbsA3:abs(iA3);
iAbsA4:abs(iA4);
if is(iA2>=0)=true then mSignA2:maOperator("+") else mSignA2:maOperator("-");
if is(iA3>=0)=true then mSignA3:maOperator("+") else mSignA3:maOperator("-");
if is(iA4>=0)=true then mSignA4:maOperator("+") else mSignA4:maOperator("-");

mUnknown: k;


oInput: ev(RESPONSE3,simp);
ob0: is(is(equal(RESPONSE3,iB))=true);
ob1: is(is(equal(RESPONSE3,ev(iD/iC,simp)))=true);
ob2: is(is(equal(RESPONSE3,ev(-iB,simp)))=true);
ob3: is(is(equal(RESPONSE3,ev(-iD/iC,simp)))=true);
