customOperator.baseValue:
s1 : make_random_state(true)$ set_random_state(s1);

load("functs");

iA: ev(random(2)+1,simp);

iB: ev(random(4)+2,simp);

iC: ev(random(2)+1,simp);

iD: ev(random(4)+2,simp);

iE: ev(random(2)+1,simp);

iF: ev(random(3)+3,simp);

for iI: 0 step 1 while iB=iD do (iD: ev(random(4)+2,simp));

for iI: 0 step 1 while iB=iF or iD=iF do (iF: ev(random(3)+3,simp));

iGA: ev(gcd(iA,iF),simp);

iGC: ev(gcd(iC,iF),simp);

iGE: ev(gcd(iE,iF),simp);

iAr: ev(iA/iGA,simp);

iFAr: ev(iF/iGA,simp);

iCr: ev(iC/iGC,simp);

iFCr: ev(iF/iGC,simp);

iEr: ev(iE/iGE,simp);

iFEr: ev(iF/iGE,simp);

iBF: ev(lcm(iB,iFAr),simp);

choose(list) := list[1+random(length(list))];

iL: ev(random(2)+2,simp);

if iL=2 then iM: ev(random(2)*2+3,simp) else iM:5;

if iM=3 then iN: ev(random(2)*2+5,simp) else iN:7;

iLM:ev(iL*iM, simp);

iMN:ev(iM*iN, simp);

iLA:ev(iL^iA, simp);

iMC:ev(iM^iC, simp);

iNE:ev(iN^iE, simp);

iLMN:ev(iLA*iMC*iNE, simp);

iDenL:ev(lcm(iB,iFAr),simp);

iDenM:ev(lcm(iB,iD,iFCr),simp);

iDenN:ev(lcm(iD,iFEr),simp);

mNumL:iFAr-ev(iAr*iB,simp);

mNumM:ev(iDenM/iB,simp)+ev(iDenM/iD,simp)-ev(iCr*iDenM/iFCr,simp);

mNumN:iFEr-ev(iEr*iD,simp);

iNumL:ev(mNumL,simp);

iNumM:ev(mNumM,simp);

iNumN:ev(mNumN,simp);

mFracL: ev(iNumL/iDenL,simp);

mFracM: ev(iNumM/iDenM,simp);

mFracN: ev(iNumN/iDenN,simp);

mAns: iL^mFracL*iM^mFracM*iN^mFracN;
