customOperator.baseValue:

mPara: ev((iQy-iPy)/(iQx-iPx),simp);
mInvPara: ev(1/mPara,simp);
mPerp: ev(-mInvPara,simp);
mGdt:mPara;
mX: x;
mY: y;
mMx: ev(mGdt*x,simp);
mC: ev(-mGdt*iPx+iPy,simp);
mAns: ev(mMx+mC,simp);
if is(mC>0)=true then mSignC:maOperator("+") else mSignC:maOperator("-");
mC: ev(abs(mC),simp);


oInput1: ev(RESPONSE1,simp);
ob10: is(is(equal(RESPONSE1,mPara))=true);
ob11: is(is(equal(RESPONSE1,mInvPara))=true);
ob12: is(is(equal(ev(RESPONSE1+mPara,simp),0))=true);
ob14: is(is(equal(RESPONSE1,mPerp))=true);
oInput: ev(RESPONSE,simp);
ob0: is(is(equal(RESPONSE,mAns))=true);
oSubstP: ev(subst(iPx,x,RESPONSE),simp);
ob1: is(is(equal(ev(oSubst-iPy,simp),0))=true);
ob2: is(is(ratnump(ev(RESPONSE+mAns,simp)))=true);
oCoeffx: ev(coeff(RESPONSE,x),simp);
ob3: is(is(equal(ev(oCoeffx-mPerp,simp),0))=true);
ob4: is(is(equal(ev(oCoeffx-mPara,simp),0))=true);
oSubstQ: ev(subst(iQx,x,RESPONSE),simp);
ob5: is(is(equal(ev(oSubst-iQy,simp),0))=true);
