customOperator.baseValue:
a : ev(iA,simp);
b : ev(iB,simp);
c : ev(iC,simp);
d : ev(iD,simp);
e : ev(iE,simp);
f : ev(iF,simp);
g : ev(iG,simp);
h : ev(iH,simp);
iAbsB: ev(abs(b),simp);
iAbsE: ev(abs(e),simp);
iAbsBD: ev(abs(iBD),simp);
iAbsBF: ev(abs(iBF),simp);
iAbsAE: ev(abs(iAE),simp);
iAbsCE: ev(abs(iCE),simp);
iAbsAG: ev(abs(iA*iG),simp);
iAbsDG: ev(abs(iD*iG),simp);
if is(b>=0)=true then mSignB:maOperator("+") else mSignB:maOperator("-");
if is(e>=0)=true then mSignE:maOperator("+") else mSignE:maOperator("-");
if is(iBD>=0)=true then mSignBD:maOperator("+") else mSignBD:maOperator("-");
if is(iBF>=0)=true then mSignBF:maOperator("+") else mSignBF:maOperator("-");
if is(iAE>=0)=true then mSignAE:maOperator("+") else mSignAE:maOperator("-");
if is(iCE>=0)=true then mSignCE:maOperator("+") else mSignCE:maOperator("-");
if is(iBD>=0)=true then mSignBDm:maOperator("-") else mSignBDm:maOperator("+");
if is(iBF>=0)=true then mSignBFm:maOperator("-") else mSignBFm:maOperator("+");
if is(iAE>=0)=true then mSignAEm:maOperator("-") else mSignAEm:maOperator("+");
if is(iCE>=0)=true then mSignCEm:maOperator("-") else mSignCEm:maOperator("+");
if is(ev(iA*iG,simp)>=0)=true then mSignAGm:maOperator("-") else mSignAGm:maOperator("+");
if is(ev(iD*iG,simp)>=0)=true then mSignDGm:maOperator("-") else mSignDGm:maOperator("+");
mQues1:ev(iA*x,simp);
mQues2:ev(iAbsB*y,simp);
mQues3:ev(iD*x,simp);
mQues4:ev(iAbsE*y,simp);
mB:ev((iC-iA*x)/iB,simp);
mE:ev((iF-iD*x)/iE,simp);
if is(iAbsB>iAbsE)=true then mSubst:mE else mSubst:mB;
